import React from 'react'
import ReactDOM from 'react-dom'

class Equipos extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menuOptions: ["Opción 1", "Opción 2", "Opción 3"],
    };
  }

  render() {
    return (
      <Menu
        options={this.state.menuOptions}
        onAddOption={this.handleAddOption.bind(this)}
      />
    );
  }

  handleAddOption() {
    this.setState({
      menuOptions: this.state.menuOptions.concat(["Nueva Opción"]),
    });
  }
}

class Menu extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let options = this.props.options;
    return (
      <div>
        <ul>
          {options.map((option) => (
            <li>{option}</li>
          ))}
        </ul>
        <button onClick={this.props.onAddOption}>Nueva Opción</button>
      </div>
    );
  }
}

ReactDOM.render(<Equipos />, document.getElementById("equipos"));
export default Equipos;
