import React from "react";

// Todo a component that creates a sidebar menu on the left side of the screen.
// the menu is expandable and collapsible.
// The menu is created from a list of items.
// Each item has a name, an icon, and a list of subitems.
// The subitems are also a list of items.

export default class SidebarData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // the list of items
      items: [
        {
          name: "Ligas",
          icon: "fas fa-futbol",
          subitems: [
            { name: "Liga 1", icon: "fas fa-futbol" },
            { name: "Liga 2", icon: "fas fa-futbol" },
            { name: "Liga 3", icon: "fas fa-futbol" },
          ],
        },
        {
          name: "Clubs",
          icon: "fas fa-futbol",
          subitems: [
            { name: "Club 1", icon: "fas fa-futbol" },
            { name: "Club 2", icon: "fas fa-futbol" },
            { name: "Club 3", icon: "fas fa-futbol" },
          ],
        },
        {
          name: "Torneos",
          icon: "fas fa-futbol",
          subitems: [
            { name: "Torneo 1", icon: "fas fa-futbol" },
            { name: "Torneo 2", icon: "fas fa-futbol" },
            { name: "Torneo 3", icon: "fas fa-futbol" },
          ],
        },
        {
          name: "Nacional",
          icon: "fas fa-futbol",
          subitems: [
            { name: "Nacional 1", icon: "fas fa-futbol" },
            { name: "Nacional 2", icon: "fas fa-futbol" },
            { name: "Nacional 3", icon: "fas fa-futbol" },
          ],
        },
        {
          name: "Jugadores",
          icon: "fas fa-futbol",
          subitems: [
            { name: "Jugador 1", icon: "fas fa-futbol" },
            { name: "Jugador 2", icon: "fas fa-futbol" },
            { name: "Jugador 3", icon: "fas fa-futbol" },
          ],
        },
      ],
      // the current selected item
      selected: 0,
      // the current selected subitem
      selectedsub: 0,
      // the current selected item when the menu is collapsed
      selectedcollapsed: 0,
      // the current selected subitem when the menu is collapsed
      selectedsubcollapsed: 0,
      // the current selected item when the menu is expanded
      selectedexpanded: 0,
      // the current selected subitem when the menu is expanded
      selectedsubexpanded: 0,
      // the current selected item when the menu is expanded and a subitem is selected
      selectedexpandedsub: 0,
      // the current selected subitem when the menu is expanded and a subitem is selected
      selectedsubexpandedsub: 0,
    };
  }
}
