import React from "react";

// Todo a component that creates a sidebar menu on the left side of the screen.
// the menu is expandable and collapsible.
// The menu is created from a list of items.
// Each item has a name, an icon, and a list of subitems.
// The subitems are also a list of items.




class SidebarItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            icon: props.icon,
            subItems: props.subItems,
            collapsed: false
        }
        this.toggleCollapse = this.toggleCollapse.bind(this);
    }

    toggleCollapse() {
        this.setState({collapsed: !this.state.collapsed});
    }

    render() {
        return (
            <li>
                <a href="#" onClick={this.toggleCollapse}>
                    <i className={this.state.icon}></i>
                    {this.state.name}
                    <span className="fa fa-chevron-right"></span>
                </a>
                {!this.state.collapsed &&
                    <ul className="list-unstyled">
                        {this.state.subItems.map((item, index) => {
                            return (
                                <SidebarSubItem
                                    key={index}
                                    name={item.name}
                                    icon={item.icon}
                                    subItems={item.subItems}
                                />
                            )
                        })}
                    </ul>
                }
            </li>
        )
    }
}
