// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.
// The sidebar menu is fixed to the left side of the screen.


import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faBars, faTimes } from "@fortawesome/free-solid-svg-icons";

export default class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebarOpen: false,
    };
    this.toggleSidebar = this.toggleSidebar.bind(this);
  }

  toggleSidebar() {
    this.setState({ sidebarOpen: !this.state.sidebarOpen });
  }

  render() {
    return (
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="sidebar-title">FÃºtbol APP</div>
          <div className="sidebar-toggle" onClick={this.toggleSidebar}>
            <FontAwesomeIcon icon={faBars} />
          </div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-close" onClick={this.toggleSidebar}>
            <FontAwesomeIcon icon={faTimes} />
          </div>
          <div className="sidebar-search">
            <input type="text" placeholder="Buscar..." />
            <div className="sidebar-search-icon">
              <FontAwesomeIcon icon={faSearch} />
            </div>
          </div>
          <div className="sidebar-link">
            <Link to="/">Inicio</Link>
          </div>
          <div className="sidebar-link">
            <Link to="/login">Login</Link>
          </div>
          <div className="sidebar-link">
            <Link to="/register">Registro</Link>
          </div>
          <div className="sidebar-link">
            <Link to="/about">Acerca de</Link>
          </div>
        </div>
        {this.state.sidebarOpen && (
          <div className="sidebar-backdrop" onClick={this.toggleSidebar} />
        )}
      </div>
    );
  }
}
