// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.



import React from "react";



export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showMenu: false,
            showSidebar: false
        };
        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
        this.showSidebar = this.showSidebar.bind(this);
        this.closeSidebar = this.closeSidebar.bind(this);
    }

    showMenu(event) {
        event.preventDefault();

        this.setState({ showMenu: true }, () => {
            document.addEventListener('click', this.closeMenu);
        });
    }

    closeMenu() {
        this.setState({ showMenu: false }, () => {
            document.removeEventListener('click', this.closeMenu);
        });
    }

    showSidebar(event) {
        event.preventDefault();

        this.setState({ showSidebar: true }, () => {
            document.addEventListener('click', this.closeSidebar);
        });
    }

    closeSidebar() {
        this.setState({ showSidebar: false }, () => {
            document.removeEventListener('click', this.closeSidebar);
        });
    }





}
