// Todo a component that creates a sidebar menu on the left side of the screen.
// The sidebar menu has a login and register button.
// The sidebar menu has a list of links to other pages.
// the sidebar is expandable and collapsable.
// The sidebar menu has a search bar.
// the sidebar has a toggle button to expand and collapse the sidebar.

import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { ButtonContainer } from "./Button";
import { ProductConsumer } from "../context";

export default class Sidebar extends Component {
  render() {
    return (
      <ProductConsumer>
        {(value) => {
          const { sidebarOpen, links, handleSidebar } = value;
          return (
            <SidebarWrapper show={sidebarOpen}>
              <ul>
                {links.map((link) => {
                  return (
                    <li key={link.id}>
                      <Link
                        to={link.path}
                        className="sidebar-link"
                        onClick={handleSidebar}
                      >
                        {link.text}
                      </Link>
                    </li>
                  );
                })}
              </ul>
              <Link to="/login" className="sidebar-link">
                <ButtonContainer onClick={() => console.log("You are logged in")}>
                  Login
                </ButtonContainer>
              </Link>
              <Link to="/register" className="sidebar-link">
                <ButtonContainer onClick={() => console.log("You are logged in")}>
                  Register
                </ButtonContainer>
              </Link>
            </SidebarWrapper>
          );
        }}
      </ProductConsumer>
    );
  }
}

const SidebarWrapper = styled.nav`
  position: fixed;
  top: 60px;
  left: 0;
  width: 100%;
  height: 100%;
  background: var(--mainGrey);
  z-index: 1;
  border-right: 4px solid var(--primaryColor);
  transition: var(--mainTransition);
  transform: ${(props) => (props.show ? "translateX(0)" : "translateX(-100%)")};
  ul {
    list-style-type: none;
    padding: 0 !important;
  }
  .sidebar-link {
    display: block;
    font-size: 1.5rem;
    text-transform: capitalize;
    color: var(--mainBlack);
    padding: 0.5rem 1.5rem;
    background: transparent;
    transition: var(--mainTransition);
  }
  .sidebar-link:hover {
    background: var(--primaryColor);
    color: var(--mainWhite);
    padding: 0.5rem 1.5rem 0.5rem 2.5rem;
    text-decoration: none;
  }
  @media (min-width: 576px) {
    width: 20rem;
  }
`;
