import React from "react";

export default class JugadoresPagina extends React.Component {

     constructor(props) {
        super(props);
        this.state = {
            jugador: []


        }
    }

    componentDidMount() {
        fetch("https://www.thesportsdb.com/api/v1/json/2/lookupplayer.php?id=34162040")
            .then(res => res.json())
            .then((data) => {
                this.setState({ jugador: data.player[0] })
            })
            .catch(console.log)
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col">
                        <div className="card">
                            <img src={this.state.jugador.strThumb} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">{this.state.jugador.strPlayer}</h5>
                                <p className="card-text">{this.state.jugador.strDescriptionEN}</p>
                            </div>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item">Fecha de nacimiento: {this.state.jugador.dateBorn}</li>
                                <li className="list-group-item">Lugar de nacimiento: {this.state.jugador.strBirthLocation}</li>
                                <li className="list-group-item">Altura: {this.state.jugador.strHeight}</li>
                                <li className="list-group-item">Peso: {this.state.jugador.strWeight}</li>
                                <li className="list-group-item">Posici√≥n: {this.state.jugador.strPosition}</li>
                                <li className="list-group-item">Pie: {this.state.jugador.strSide}</li>
                                <li className="list-group-item">Equipo: {this.state.jugador.strTeam}</li>
                            </ul>
                            <div className="card-body">
                                <a href={this.state.jugador.strFacebook} className="card-link">Facebook</a>
                                <a href={this.state.jugador.strTwitter} className="card-link">Twitter</a>
                                <a href={this.state.jugador.strInstagram} className="card-link">Instagram</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
