import React from "react";

// Todo a component that creates a sidebar menu on the left side of the screen.
// the menu is expandable and collapsible.
// The menu is created from a list of items.
// Each item has a name, an icon, and a list of subitems.
// The subitems are also a list of items.


export default class SidebarData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            menuItems: [
                {
                    name: "Torneos",
                    icon: "fa fa-trophy",
                    subItems: [
                        {
                            name: "Torneo Nacional",
                            icon: "fa fa-trophy",
                            subItems: [
                                {
                                    name: "Torneo Nacional",
                                    icon: "fa fa-trophy",
                                    subItems: []
                                }
                            ]
                        },
                        {
                            name: "Torneo Internacional",
                            icon: "fa fa-trophy",
                            subItems: [
                                {
                                    name: "Torneo Internacional",
                                    icon: "fa fa-trophy",
                                    subItems: []
                                }
                            ]
                        }
                    ]
                },
                {
                    name: "Ligas",
                    icon: "fa fa-list",
                    subItems: [
                        {
                            name: "Liga Nacional",
                            icon: "fa fa-list",
                            subItems: [
                                {
                                    name: "Liga Nacional",
                                    icon: "fa fa-list",
                                    subItems: []
                                }
                            ]
                        },
                        {
                            name: "Liga Internacional",
                            icon: "fa fa-list",
                            subItems: [
                                {
                                    name: "Liga Internacional",
                                    icon: "fa fa-list",
                                    subItems: []
                                }
                            ]
                        }
                    ]
                },
                {
                    name: "Clubs",
                    icon: "fa fa-home",
                    subItems: [
                        {
                            name: "Club Nacional",
                            icon: "fa fa-home",
                            subItems: [
                                {
                                    name: "Club Nacional",
                                    icon: "fa fa-home",
                                    subItems: []
                                }
                            ]
                        },
                        {
                            name: "Club Internacional",
                            icon: "fa fa-home",
                            subItems: [
                                {
                                    name: "Club Internacional",
                                    icon: "fa fa-home",
                                    subItems: []
                                }
                            ]
                        }
                    ]
                },
                {
                    name: "Nacional",
                    icon: "fa fa-flag",
                    subItems: [
                        {
                            name: "Nacional",
                            icon: "fa fa-flag",
                            subItems: [
                                {
                                    name: "Nacional",
                                    icon: "fa fa-flag",
                                    subItems: []
                                }
                            ]
                        }
                    ]
                },
                {
                    name: "Internacional",
                    icon: "fa fa-globe",
                    subItems: [
                        {
                            name: "Internacional",
                            icon: "fa fa-globe",
                            subItems: [
                                {
                                    name: "Internacional",
                                    icon: "fa fa-globe",
                                    subItems: []
                                }
                            ]
                        }
                    ]
                }
            ]
        }
    }

    render() {
        return (
            <div className="sidebar">
                <div className="sidebar-header">
                    <h3>Artio</h3>
                </div>

                <ul className="list-unstyled components">
                    {this.state.menuItems.map((item, index) => {
                        return (
                            <SidebarItem
                                key={index}
                                name={item.name}
                                icon={item.icon}
                                subItems={item.subItems}
                            />
                        )
                    })}
                </ul>
            </div>
        )
    }
}
